import React, { Component } from "react";
import Home from "./components/Home";
import Resume from "./components/Resume";
import Form from "./components/Form";
import Card from "./components/Card";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

import "../src/styles/App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bcard: {
        name: "",
        phone: "",
        profession: "",
        email: ""
      },
      details: []
    };
  }

  hanldeChange = e => {
    const { name, value } = e.target;

    this.setState(prevState => ({
      bcard: { ...prevState.post, [name]: value }
    }));
  };

  hanldeSubmit = e => {
    e.preventDefault();

    this.setState(prevState => ({
      details: [...prevState.details, prevState.bcard],
      bcard: { name: "", phone: "", email: "", profession: "" }
    }));
  };

  render() {
    return (
      <Router>
        <>
          <Link to="/resume">
            <button className="app__button"> Resume </button>
          </Link>
          <Link className="app__button" to="/card">
            <button className="app__button"> Card </button>
          </Link>
          <Link className="app__button" to="/createnew">
            <button className="app__button"> Createnew </button>
          </Link>
        </>
        <Switch>
          <Route path="/home">
            <Home />
          </Route>
          <Route path="/resume">
            <Resume />
          </Route>
          <Route path="/createnew" exact>
            <Form
              hanldeChange={this.handleChange}
              details={this.state.details}
              hanldeSubmit={this.handleSubmit}
            />
          </Route>
          <Route path="/card">
            <Card />
          </Route>
        </Switch>
      </Router>
    );
  }
}
export default App;
